tables.table_schema:
CREATE TABLE infrastructure (
  id INT PRIMARY KEY,
  city VARCHAR,
  category VARCHAR,
  maintenance_date DATE
);

CREATE TABLE maintenance_activities (
  activity_id INT PRIMARY KEY,
  activity_name VARCHAR,
  duration_hours INT,
  cost DECIMAL
);

CREATE TABLE equipment (
  equipment_id INT PRIMARY KEY,
  equipment_name VARCHAR,
  brand VARCHAR,
  purchase_date DATE
);

CREATE TABLE Cities (
    city_id INT PRIMARY KEY,
    city_name TEXT NOT NULL,
    population INT
);

CREATE TABLE Infrastructure (
    infra_id INT PRIMARY KEY,
    city_id INT REFERENCES Cities(city_id),
    infra_type TEXT,
    infra_desc TEXT
);

CREATE TABLE Maintenance (
    maintenance_id INT PRIMARY KEY,
    infra_id INT REFERENCES Infrastructure(infra_id),
    maintenance_date DATE,
    maintenance_details TEXT
);

CREATE TABLE MaintenanceTeams (
    team_id INT PRIMARY KEY,
    team_name TEXT NOT NULL,
    team_lead TEXT
);

CREATE TABLE MaintenanceAssignments (
    assign_id INT PRIMARY KEY,
    maintenance_id INT REFERENCES Maintenance(maintenance_id),
    team_id INT REFERENCES MaintenanceTeams(team_id),
    assign_date DATE
);
