CREATE TABLE MeasurementCategories ( 
 category_id UUID PRIMARY KEY, 
 category_name VARCHAR(50), 
 description TEXT 
 );

CREATE TABLE MonitoringStations (
    station_id INT PRIMARY KEY,
    station_name VARCHAR(50) NOT NULL,
