);

CREATE TABLE Temperature (
    temperature_id INT PRIMARY KEY,
    station_id INT NOT NULL,
    temperature_value FLOAT,
    temperature_unit VARCHAR(10)
);

CREATE TABLE Humidity (
    humidity_id INT PRIMARY KEY,
    station_id INT NOT NULL,
    humidity_value FLOAT
);

CREATE TABLE monitoring_stations (
  station_id INT PRIMARY KEY,
  station_name VARCHAR(255),
  location_lat DECIMAL,
  location_long DECIMAL
);

CREATE TABLE pollutants (
  pollutant_id INT PRIMARY KEY,
  pollutant_name VARCHAR(50),
  pollutant_description TEXT
);

CREATE TABLE monitoring_areas (
  area_id INT PRIMARY KEY,
  area_name VARCHAR(100),
  area_description TEXT
);
