tables.table_schema:
CREATE TABLE waste_collection_schedule (
    schedule_id INT PRIMARY KEY,
    pickup_location TEXT,
    pickup_date DATE,
    pickup_time TIME
);

CREATE TABLE recycling_pickups (
    pickup_id INT PRIMARY KEY,
    recycling_bin_id INT,
    pickup_date DATE,
    pickup_time TIME
);

CREATE TABLE pickup_vehicles (
    vehicle_id INT PRIMARY KEY,
    vehicle_type TEXT,
    capacity INT
);

CREATE TABLE routes_optimization (
    route_id INT PRIMARY KEY,
    pickup_schedule_id INT,
    optimization_date DATE,
    optimization_time TIME
);
