tables.table_schema:
CREATE TABLE renewable_energy_production (
  id INT PRIMARY KEY,
  energy_source VARCHAR,
  production_amount DECIMAL,
  production_date DATE
);

CREATE TABLE energy_consumption (
  id INT PRIMARY KEY,
  location VARCHAR,
  consumption_amount DECIMAL,
  consumption_date DATE
);

CREATE TABLE production (
    id SERIAL PRIMARY KEY,
    date DATE NOT NULL,
    energy_source VARCHAR(50) NOT NULL,
    energy_production DOUBLE PRECISION
);

CREATE TABLE consumption (
    id SERIAL PRIMARY KEY,
    date DATE NOT NULL,
    location VARCHAR(50) NOT NULL,
    energy_consumed DOUBLE PRECISION
);

CREATE TABLE progress (
    id SERIAL PRIMARY KEY,
    date DATE NOT NULL,
    goal VARCHAR(50) NOT NULL,
    achieved BOOLEAN
);

CREATE TABLE Production (
    id INT PRIMARY KEY,
    energy_source VARCHAR(255),
    production_date DATE,
    amount DECIMAL
);

CREATE TABLE Consumption (
    id INT PRIMARY KEY,
    device_id INT,
    consumption_date DATE,
    amount DECIMAL
);

CREATE TABLE Devices (
    id INT PRIMARY KEY,
    device_name VARCHAR(255),
    device_type VARCHAR(255)
);

CREATE TABLE Users (
    id INT PRIMARY KEY,
    username VARCHAR(255),
    email VARCHAR(255),
    role VARCHAR(50)
);

CREATE TABLE energy_production (
    id INT PRIMARY KEY,
    location VARCHAR(100),
    production_date DATE,
    production_amount DECIMAL
);

CREATE TABLE energy_consumption (
    id INT PRIMARY KEY,
    location VARCHAR(100),
    consumption_date DATE,
    consumption_amount DECIMAL
);

CREATE TABLE energy_sources (
    id INT PRIMARY KEY,
    source_name VARCHAR(100),
    source_type VARCHAR(50),
    installation_date DATE
);

CREATE TABLE energy_storage (
    id INT PRIMARY KEY,
    location VARCHAR(100),
    storage_capacity DECIMAL,
    storage_level DECIMAL
);

CREATE TABLE sustainability_goals (
    id INT PRIMARY KEY,
    goal_name VARCHAR(100),
    target_date DATE,
    target_amount DECIMAL
);
