tables.table_schema:
CREATE TABLE Environmental_Compliance_Monitoring (
  id INT,
  company_name VARCHAR,
  location VARCHAR,
  compliance_status BOOLEAN
);

CREATE TABLE Environmental_Compliance_Scorecards (
    company_id UUID PRIMARY KEY,
    compliance_score INT,
    last_review_date DATE
);

CREATE TABLE Environmental_Regulations (
    regulation_id UUID PRIMARY KEY,
    regulation_name VARCHAR(50),
    legal_requirements TEXT,
    due_date DATE
);

CREATE TABLE regulations (
  regulation_id INT PRIMARY KEY,
  regulation_name VARCHAR(255),
  description TEXT
);

CREATE TABLE ComplianceRecord (
    record_id UUID PRIMARY KEY,
    location_id UUID,
    timestamp TIMESTAMP,
    compliant BOOLEAN
);

CREATE TABLE Regulations (
    regulation_id UUID PRIMARY KEY,
    regulation_name VARCHAR(50),
    description TEXT
);

CREATE TABLE ComplianceHistory (
    history_id UUID PRIMARY KEY,
    record_id UUID,
    regulation_id UUID,
    compliance_status VARCHAR(20)
);

CREATE TABLE Locations (
    location_id UUID PRIMARY KEY,
    location_name VARCHAR(100),
    region VARCHAR(50)
);

CREATE TABLE regulations (
    regulation_id INT PRIMARY KEY,
    regulation_name VARCHAR(255) NOT NULL,
    description TEXT
);

CREATE TABLE standards (
    standard_id INT PRIMARY KEY,
    standard_name VARCHAR(255) NOT NULL,
    category VARCHAR(100) NOT NULL
);

CREATE TABLE companies (
    company_id INT PRIMARY KEY,
    company_name VARCHAR(255) NOT NULL,
    location VARCHAR(100)
);

CREATE TABLE audits (
    audit_id INT PRIMARY KEY,
    audit_date DATE NOT NULL,
    company_id INT,
    FOREIGN KEY (company_id) REFERENCES companies(company_id)
);
