tables.table_schema:
CREATE TABLE emissions_reports (
  report_id SERIAL PRIMARY KEY,
  organization_id INT NOT NULL,
  emission_date DATE NOT NULL,
  emission_source VARCHAR(100) NOT NULL,
  emission_amount DECIMAL(10,2) NOT NULL
);

CREATE TABLE carbon_footprint (
  footprint_id SERIAL PRIMARY KEY,
  organization_id INT NOT NULL,
  footprint_date DATE NOT NULL,
  footprint_value DECIMAL(10,2) NOT NULL
);

CREATE TABLE organizations (
    id INT NOT NULL PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    industry VARCHAR(255) NOT NULL,
    location VARCHAR(255) NOT NULL
);

CREATE TABLE Organizations (
    id INT PRIMARY KEY,
    name VARCHAR,
    location VARCHAR,
    industry VARCHAR
);

CREATE TABLE EmissionsData (
    org_name VARCHAR(255),
    emission_date DATE,
    emission_type VARCHAR(50),
    emission_amount DECIMAL(10,2)
);

CREATE TABLE Organizations (
    org_id SERIAL PRIMARY KEY,
    org_name VARCHAR(255),
    org_type VARCHAR(50)
);

CREATE TABLE EmissionSources (
    source_id SERIAL PRIMARY KEY,
    source_name VARCHAR(255),
    source_category VARCHAR(50)
);

CREATE TABLE ReportingLogs (
    log_id SERIAL PRIMARY KEY,
    org_id INT REFERENCES Organizations(org_id),
    log_date DATE,
    log_message TEXT
);
