tables.table_schema:
CREATE TABLE employee_attendance (
    id INT PRIMARY KEY,
    employee_id INT NOT NULL,
    timestamp TIMESTAMP NOT NULL,
    biometric_identifier VARCHAR(50) NOT NULL
);
CREATE TABLE biometric_data (
    biometric_id INT PRIMARY KEY,
    employee_id INT NOT NULL,
    fingerprint BLOB NOT NULL,
    palmprint BLOB NOT NULL
);

CREATE TABLE EmployeeAttendance (
    employee_id INT PRIMARY KEY,
    biometric_data BLOB,
    check_in_time TIMESTAMP,
    check_out_time TIMESTAMP
);

CREATE TABLE BiometricIdentifiers (
    employee_id INT PRIMARY KEY,
    fingerprint_data BLOB,
    iris_scan_data BLOB,
    facial_recognition_data BLOB
);

CREATE TABLE Employee (
    employee_id INT PRIMARY KEY,
    employee_name VARCHAR(50),
    biometric_id INT
);

CREATE TABLE BiometricData (
    biometric_id INT PRIMARY KEY,
    fingerprint_data VARCHAR(100)
);

CREATE TABLE Logs (
    log_id INT PRIMARY KEY,
    employee_id INT,
    action VARCHAR(50),
    timestamp TIMESTAMP
);

CREATE TABLE Employees (
  employee_id INT PRIMARY KEY,
  name VARCHAR(50) NOT NULL,
  biometric_id VARCHAR(20) UNIQUE
);

CREATE TABLE Attendance (
  attendance_id INT PRIMARY KEY,
  employee_id INT,
  check_in TIMESTAMP,
  check_out TIMESTAMP
);

CREATE TABLE BiometricData (
  biometric_id VARCHAR(20) PRIMARY KEY,
  fingerprint_template BLOB,
  retina_scan_template BLOB
);

CREATE TABLE Departments (
  department_id INT PRIMARY KEY,
  department_name VARCHAR(50) NOT NULL
);

CREATE TABLE EmployeeDepartments (
  employee_id INT,
  department_id INT,
  PRIMARY KEY (employee_id, department_id)
);
