tables.table_schema:
CREATE TABLE biometric_data (
    user_id INT PRIMARY KEY,
    fingerprint_data BYTEA,
    iris_scan_data BYTEA,
    face_recognition_data BYTEA
);

CREATE TABLE biometric_data (
    id INT PRIMARY KEY,
    user_id INT NOT NULL,
    biometric_type VARCHAR(50) NOT NULL,
    biometric_data BYTEA NOT NULL
);

CREATE TABLE user_accounts (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) NOT NULL,
    password VARCHAR(100) NOT NULL
);

CREATE TABLE financial_transactions (
    transaction_id INT PRIMARY KEY,
    user_id INT NOT NULL,
    transaction_amount DECIMAL(10,2) NOT NULL,
    transaction_date TIMESTAMP NOT NULL
);

CREATE TABLE biometric_data (
    id INT PRIMARY KEY,
    user_id INT NOT NULL,
    biometric_type VARCHAR(50) NOT NULL,
    biometric_data BLOB NOT NULL
);

CREATE TABLE user_info (
    user_id INT PRIMARY KEY,
    full_name VARCHAR(100) NOT NULL,
    email VARCHAR(100) UNIQUE NOT NULL,
    phone_number VARCHAR(20) NOT NULL
);

CREATE TABLE transaction_history (
    transaction_id INT PRIMARY KEY,
    user_id INT NOT NULL,
    transaction_amount DECIMAL(10, 2) NOT NULL,
    transaction_date TIMESTAMP NOT NULL
);

CREATE TABLE user_data (
    user_id INT PRIMARY KEY,
    username VARCHAR(100) NOT NULL,
    biometric_data BYTEA NOT NULL,
    verification_status BOOLEAN
);

CREATE TABLE transaction_data (
    transaction_id INT PRIMARY KEY,
    user_id INT REFERENCES user_data(user_id),
    transaction_amount DECIMAL(10, 2) NOT NULL,
    transaction_date DATE
);

CREATE TABLE identity_verification (
    verification_id INT PRIMARY KEY,
    user_id INT REFERENCES user_data(user_id),
    verification_type VARCHAR(50) NOT NULL,
    verification_date TIMESTAMP
);

CREATE TABLE biometric_logs (
    log_id INT PRIMARY KEY,
    user_id INT REFERENCES user_data(user_id),
    log_type VARCHAR(50) NOT NULL,
    log_timestamp TIMESTAMP
);

CREATE TABLE financial_institutions (
    institution_id INT PRIMARY KEY,
    institution_name VARCHAR(100) NOT NULL,
    institution_type VARCHAR(50) NOT NULL
);
