tables.table_schema:
CREATE TABLE HeartRate ( 
    id SERIAL PRIMARY KEY, 
    patient_id INT REFERENCES Patient(id), 
    timestamp TIMESTAMP NOT NULL, 
    bpm INT NOT NULL 
);

CREATE TABLE BloodSugar ( 
    id SERIAL PRIMARY KEY, 
    patient_id INT REFERENCES Patient(id), 
    timestamp TIMESTAMP NOT NULL, 
    level FLOAT NOT NULL 
);

CREATE TABLE Temperature ( 
    id SERIAL PRIMARY KEY, 
    patient_id INT REFERENCES Patient(id), 
    timestamp TIMESTAMP NOT NULL, 
    celsius FLOAT NOT NULL 
);
