tables.table_schema:
CREATE TABLE geofencing_areas (
  id INT PRIMARY KEY,
  name VARCHAR(255) NOT NULL,
  coordinates VARCHAR(255) NOT NULL
);

CREATE TABLE users (
  user_id INT PRIMARY KEY,
  username VARCHAR(255) NOT NULL,
  email VARCHAR(255) NOT NULL
);

CREATE TABLE notifications (
  notification_id INT PRIMARY KEY,
  user_id INT NOT NULL,
  geofence_id INT NOT NULL,
  action_type ENUM('enter', 'exit') NOT NULL
);

CREATE TABLE geofencing_areas (
  id UUID PRIMARY KEY,
  name VARCHAR(255),
  area geography
);

CREATE TABLE users (
  id UUID PRIMARY KEY,
  username VARCHAR(255),
  email VARCHAR(255)
);

CREATE TABLE Users (
    user_id UUID NOT NULL,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) NOT NULL,
    PRIMARY KEY (user_id)
);

CREATE TABLE User_Geofence_Entries (
    user_id UUID NOT NULL,
    geofence_id UUID NOT NULL,
    entry_timestamp TIMESTAMP NOT NULL,
    PRIMARY KEY (user_id, geofence_id, entry_timestamp)
);

CREATE TABLE User_Geofence_Exits (
    user_id UUID NOT NULL,
    geofence_id UUID NOT NULL,
    exit_timestamp TIMESTAMP NOT NULL,
    PRIMARY KEY (user_id, geofence_id, exit_timestamp)
);
