tables.table_schema:
CREATE TABLE quantum_research_projects (
    project_id INT PRIMARY KEY,
    project_name VARCHAR(255) NOT NULL,
    project_description TEXT,
    project_status VARCHAR(50)
);

CREATE TABLE researchers (
    researcher_id INT PRIMARY KEY,
    researcher_name VARCHAR(255) NOT NULL,
    researcher_email VARCHAR(255),
    researcher_field VARCHAR(100)
);

CREATE TABLE researchers (
  researcher_id UUID PRIMARY KEY,
  researcher_name STRING,
  researcher_position STRING,
  department_id UUID
);

CREATE TABLE results (
  result_id UUID PRIMARY KEY,
  experiment_id UUID,
  result_data STRING
);

CREATE TABLE researchers (
  researcher_id SERIAL PRIMARY KEY,
  researcher_name VARCHAR(255) NOT NULL,
  researcher_email VARCHAR(255),
  research_field VARCHAR(100)
);

CREATE TABLE project_researchers (
  project_id INT REFERENCES projects(project_id),
  researcher_id INT REFERENCES researchers(researcher_id)
);

CREATE TABLE experiment_results (
  result_id SERIAL PRIMARY KEY,
  experiment_id INT REFERENCES experiments(experiment_id),
  result_date DATE,
  result_details TEXT
);
