tables.table_schema:
CREATE TABLE quantum_error_correction_algorithm (
    algorithm_id INT PRIMARY KEY,
    algorithm_name VARCHAR(50) NOT NULL,
    algorithm_description TEXT
);

CREATE TABLE error_correction_codes (
    code_id INT PRIMARY KEY,
    code_type VARCHAR(50) NOT NULL,
    code_properties TEXT
);

CREATE TABLE quantum_code (
    code_id SERIAL PRIMARY KEY,
    code_name VARCHAR(255) NOT NULL,
    error_type VARCHAR(100) NOT NULL
);

CREATE TABLE qubit (
    qubit_id SERIAL PRIMARY KEY,
    qubit_value FLOAT NOT NULL,
    code_id INTEGER NOT NULL
);

CREATE TABLE syndromes (
    syndrome_id SERIAL PRIMARY KEY,
    syndrome_info TEXT NOT NULL,
    code_id INTEGER NOT NULL,
    qubit_id INTEGER NOT NULL
);

CREATE TABLE QuantumCodeWords (
    code_word_id INT PRIMARY KEY,
    code_word VARCHAR(255) NOT NULL
);

CREATE TABLE SyndromeTable (
    syndrome_id INT PRIMARY KEY,
    syndrome_code VARCHAR(255) NOT NULL
);

CREATE TABLE ErrorMatrix (
    error_id INT PRIMARY KEY,
    error_type VARCHAR(255) NOT NULL,
    error_description TEXT
);

CREATE TABLE CorrectionMatrix (
    correction_id INT PRIMARY KEY,
    correction_type VARCHAR(255) NOT NULL,
    correction_rule TEXT
);

CREATE TABLE quantum_error_correction_algorithms (
  algorithm_id SERIAL PRIMARY KEY,
  algorithm_name VARCHAR(50) NOT NULL,
  description TEXT
);

CREATE TABLE qubits (
  qubit_id SERIAL PRIMARY KEY,
  qubit_value INT NOT NULL,
  is_entangled BOOLEAN
);

CREATE TABLE stabilizer_codes (
  code_id SERIAL PRIMARY KEY,
  code_name VARCHAR(50) NOT NULL,
  generator_matrix TEXT
);

CREATE TABLE logical_qubits (
  logical_qubit_id SERIAL PRIMARY KEY,
  logical_qubit_value INT NOT NULL,
  has_error_correction BOOLEAN
);

CREATE TABLE error_syndromes (
  syndrome_id SERIAL PRIMARY KEY,
  syndrome_name VARCHAR(50) NOT NULL,
  syndrome_description TEXT
);
