tables.table_schema:
CREATE TABLE lights_schedule (
    id INT PRIMARY KEY,
    light_name VARCHAR(50) NOT NULL,
    turn_on_time TIMESTAMP NOT NULL,
    turn_off_time TIMESTAMP NOT NULL
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) NOT NULL
);

CREATE TABLE tasks (
    task_id INT PRIMARY KEY,
    task_name VARCHAR(50) NOT NULL,
    task_description TEXT
);

CREATE TABLE schedule_logs (
    log_id INT PRIMARY KEY,
    task_id INT NOT NULL,
    log_timestamp TIMESTAMP NOT NULL,
    log_message TEXT
);

CREATE TABLE LightSchedules (
    light_id INT PRIMARY KEY,
    schedule_id INT,
    scheduled_time TIMESTAMP,
    is_active BOOL
);

CREATE TABLE ScheduleTypes (
    schedule_type_id INT PRIMARY KEY,
    schedule_type_name VARCHAR(50) UNIQUE
);

CREATE TABLE TaskLogs (
    log_id INT PRIMARY KEY,
    task_id INT,
    execution_time TIMESTAMP,
    status VARCHAR(20)
);

CREATE TABLE Tasks (
    task_id INT PRIMARY KEY,
    task_name VARCHAR(50) UNIQUE,
    description TEXT,
    is_enabled BOOL
);

CREATE TABLE TaskSchedules (
    task_id INT,
    schedule_id INT,
    day_of_week INT,
    PRIMARY KEY(task_id, schedule_id)
);
