tables.table_schema:
CREATE TABLE sensors (
  sensor_id INT PRIMARY KEY,
  sensor_type VARCHAR(50),
  sensor_location VARCHAR(100)
);

CREATE TABLE users (
  user_id INT PRIMARY KEY,
  username VARCHAR(50),
  email VARCHAR(100)
);

CREATE TABLE doors (
  door_id INT PRIMARY KEY,
  door_location VARCHAR(100),
  door_status VARCHAR(10)
);

CREATE TABLE windows (
  window_id INT PRIMARY KEY,
  window_location VARCHAR(100),
  window_status VARCHAR(10)
);
