);

CREATE TABLE user_preferences (
  user_id INT PRIMARY KEY,
  lighting_pref VARCHAR(50),
  temperature_pref VARCHAR(50),
  music_pref VARCHAR(50)
);

CREATE TABLE lighting_devices (
  device_id INT PRIMARY KEY,
  device_name VARCHAR(50),
  brightness_level INT
);

CREATE TABLE temperature_devices (
  device_id INT PRIMARY KEY,
  device_name VARCHAR(50),
  current_temperature FLOAT
);

CREATE TABLE music_devices (
  device_id INT PRIMARY KEY,
  device_name VARCHAR(50),
  volume_level INT
);

CREATE TABLE UserPreferences ( 
 user_id UUID PRIMARY KEY, 
 lighting_pref VARCHAR(50), 
 temperature_pref VARCHAR(50), 
 music_pref VARCHAR(50) 
 );

CREATE TABLE HouseLayout ( 
 house_id UUID PRIMARY KEY, 
 room_name VARCHAR(50), 
 layout_desc VARCHAR(255) 
 );

CREATE TABLE DeviceStatus ( 
 device_id UUID PRIMARY KEY, 
 status VARCHAR(50), 
 last_updated TIMESTAMPTZ, 
 user_id UUID REFERENCES UserPreferences(user_id) 
 );

CREATE TABLE NotificationSettings ( 
 user_id UUID PRIMARY KEY REFERENCES UserPreferences(user_id), 
 email_pref BOOLEAN, 
 sms_pref BOOLEAN 
 );
