tables.table_schema:
CREATE TABLE keyword_targets (
	id INT PRIMARY KEY,
	keyword VARCHAR(255) NOT NULL,
	campaign_id INT,
	bid_amount DECIMAL(10, 2) NOT NULL
);

CREATE TABLE campaign_keywords (
    id UUID PRIMARY KEY,
    campaign_id UUID,
    keyword VARCHAR(255)
);

CREATE TABLE keywords (
    id UUID PRIMARY KEY,
    keyword VARCHAR(255),
    search_volume INT,
    average_cpc DECIMAL(10,2)
);

CREATE TABLE keyword_campaigns (
    campaign_id INT PRIMARY KEY,
    campaign_name TEXT,
    keyword_id INT,
    keyword TEXT
);

CREATE TABLE campaigns (
    campaign_id INT PRIMARY KEY,
    campaign_name TEXT,
    start_date DATE,
    end_date DATE
);

CREATE TABLE keywords (
    keyword_id INT PRIMARY KEY,
    keyword TEXT,
    search_volume INT,
    bidding_cpc DECIMAL
);

CREATE TABLE ad_groups (
    id INT PRIMARY KEY,
    campaign_id INT,
    name TEXT,
    status TEXT
);
