CREATE TABLE advertising_creatives (
    id INT PRIMARY KEY,
    creative_name VARCHAR(255),
    message TEXT,
    conversion_rate DECIMAL(5,2)
);

CREATE TABLE creative_a (
    id INT PRIMARY KEY,
    creative_name VARCHAR(255),
    messaging TEXT
);

CREATE TABLE creative_b (
    id INT PRIMARY KEY,
    creative_name VARCHAR(255),
    messaging TEXT
);

CREATE TABLE test_results (
    id INT PRIMARY KEY,
    creative_id INT,
    conversion_rate DECIMAL,
    test_group VARCHAR(50)
);

CREATE TABLE user_feedback (
    id INT PRIMARY KEY,
    creative_id INT,
    user_id INT,
    feedback TEXT
);
