tables.table_schema:
CREATE TABLE marketing_channels (
    id INT PRIMARY KEY,
    channel_name VARCHAR(255),
    sales_amount DECIMAL(10, 2),
    conversions INT
);

CREATE TABLE marketing_channels (
    channel_id INT PRIMARY KEY,
    channel_name VARCHAR(255) NOT NULL,
    channel_description TEXT
);

CREATE TABLE marketing_channels (
    channel_id UUID PRIMARY KEY,
    channel_name VARCHAR(255) NOT NULL
);

CREATE TABLE conversions_data (
    conversion_id UUID PRIMARY KEY,
    conversion_date DATE NOT NULL,
    channel_id UUID NOT NULL,
    FOREIGN KEY (channel_id) REFERENCES marketing_channels(channel_id)
);

CREATE TABLE roi_analysis (
    roi_id UUID PRIMARY KEY,
    roi_value DECIMAL NOT NULL,
    conversion_id UUID NOT NULL,
    FOREIGN KEY (conversion_id) REFERENCES conversions_data(conversion_id)
);

CREATE TABLE marketing_campaigns (
    campaign_id UUID PRIMARY KEY,
    campaign_name VARCHAR(255) NOT NULL,
    start_date DATE NOT NULL,
    end_date DATE NOT NULL
);
