tables.table_schema:
CREATE TABLE Families (
  id INT PRIMARY KEY,
  family_name VARCHAR(50),
  address VARCHAR(100),
  contact_number VARCHAR(15)
);

CREATE TABLE SuppliesNeeded (
  id INT PRIMARY KEY,
  supply_name VARCHAR(50),
  quantity INT,
  family_id INT
);

CREATE TABLE AidOrganizations (
  id INT PRIMARY KEY,
  organization_name VARCHAR(50),
  contact_person VARCHAR(50),
  contact_number VARCHAR(15)
);
