tables.table_schema:
CREATE TABLE disaster_areas (
  id INT PRIMARY KEY,
  area_name VARCHAR(50),
  affected_population INT,
  resource_needs TEXT
);

CREATE TABLE relief_resources (
  resource_id INT PRIMARY KEY,
  resource_type VARCHAR(50),
  quantity_available INT,
  location VARCHAR(50)
);

CREATE TABLE Disasters (
  id INT PRIMARY KEY,
  date DATE,
  location VARCHAR,
  severity VARCHAR
);

CREATE TABLE Resources (
  id INT PRIMARY KEY,
  name VARCHAR,
  type VARCHAR,
  quantity INT
);

CREATE TABLE PriorityAreas (
  id INT PRIMARY KEY,
  area_name VARCHAR,
  priority_level INT,
  status VARCHAR
);

CREATE TABLE affected_areas (
    area_id INT PRIMARY KEY,
    area_name VARCHAR(50),
    disaster_type VARCHAR(50),
    affected_population INT
);

CREATE TABLE relief_efforts (
    effort_id INT PRIMARY KEY,
    area_id INT REFERENCES affected_areas(area_id),
    organization_name VARCHAR(50),
    donation_amount DECIMAL
);

CREATE TABLE resource_allocation (
    allocation_id INT PRIMARY KEY,
    effort_id INT REFERENCES relief_efforts(effort_id),
    resource_type VARCHAR(50),
    quantity INT
);

CREATE TABLE volunteer_info (
    volunteer_id INT PRIMARY KEY,
    name VARCHAR(50),
    area_id INT REFERENCES affected_areas(area_id),
    contact_number VARCHAR(15)
);

CREATE TABLE action_plan (
    plan_id INT PRIMARY KEY,
    effort_id INT REFERENCES relief_efforts(effort_id),
    plan_description TEXT,
    completion_date DATE
);
