tables.table_schema:
CREATE TABLE remote_work_expenses (
 id INT PRIMARY KEY,
 receipt_image BYTEA,
 approval_status VARCHAR(255),
 reimbursement_amount DECIMAL
);

CREATE TABLE reimbursements (
    reimbursement_id INT PRIMARY KEY,
    approval_id INT,
    amount DECIMAL(10, 2),
    payment_date DATE
);

CREATE TABLE employees (
    employee_id INT PRIMARY KEY,
    employee_name VARCHAR(255),
    department VARCHAR(100),
    email VARCHAR(100)
);
