tables.table_schema:
CREATE TABLE EmployeeBenefits (
    employee_id INT PRIMARY KEY,
    benefits_package TEXT,
    compensation DECIMAL
);

CREATE TABLE CompensationHistory (
    history_id SERIAL PRIMARY KEY,
    employee_id INT,
    compensation DECIMAL,
    effective_date DATE
);

CREATE TABLE EmployeeBenefits (
    employee_id INT PRIMARY KEY,
    benefit_type VARCHAR,
    benefit_amount DECIMAL
);

CREATE TABLE CompensationPackages (
    package_id INT PRIMARY KEY,
    base_salary DECIMAL,
    bonus DECIMAL
);

CREATE TABLE EmployeeCompensation (
    employee_id INT,
    package_id INT,
    FOREIGN KEY (employee_id) REFERENCES EmployeeBenefits(employee_id),
    FOREIGN KEY (package_id) REFERENCES CompensationPackages(package_id)
);

CREATE TABLE benefits (
    benefit_id INT PRIMARY KEY,
    benefit_name VARCHAR(255) NOT NULL,
    description TEXT,
    cost DECIMAL(10, 2) NOT NULL
);

CREATE TABLE employee_benefits (
	employee_id INT PRIMARY KEY,
	benefits_description TEXT,
	compensation_package DECIMAL
);

CREATE TABLE health_insurance (
	policy_number VARCHAR PRIMARY KEY,
	employee_id INT,
	policy_provider VARCHAR,
	coverage_type VARCHAR
);

CREATE TABLE retirement_benefits (
	plan_id INT PRIMARY KEY,
	employee_id INT,
	plan_type VARCHAR,
	contributions DECIMAL
);

CREATE TABLE paid_time_off (
	request_id INT PRIMARY KEY,
	employee_id INT,
	start_date DATE,
	end_date DATE
);

CREATE TABLE bonuses (
	bonus_id INT PRIMARY KEY,
	employee_id INT,
	bonus_amount DECIMAL,
	reason TEXT
);
