tables.table_schema:
CREATE TABLE player_achievements (
    player_id INT,
    achievement_id INT,
    achievement_name VARCHAR(50),
    date_achieved DATE
);

CREATE TABLE achievements (
    player_id INT PRIMARY KEY,
    achievement_name VARCHAR(255) NOT NULL,
    achievement_date DATE
);

CREATE TABLE achievements (
    achievement_id INT PRIMARY KEY,
    player_id INT,
    achievement_title VARCHAR(255),
    achievement_date DATE
);

CREATE TABLE trophies (
    trophy_id INT PRIMARY KEY,
    player_id INT,
    trophy_name VARCHAR(255),
    trophy_date DATE
);

CREATE TABLE leaderboard (
    player_id INT PRIMARY KEY,
    player_name VARCHAR(255),
    total_achievements INT,
    total_trophies INT
);

CREATE TABLE player (
  player_id INT PRIMARY KEY,
  player_name VARCHAR(50) NOT NULL,
  country VARCHAR(50) NOT NULL
);

CREATE TABLE achievement (
  achievement_id INT PRIMARY KEY,
  achievement_name VARCHAR(50) NOT NULL
);

CREATE TABLE trophy (
  trophy_id INT PRIMARY KEY,
  trophy_name VARCHAR(50) NOT NULL,
  trophy_points INT
);

CREATE TABLE player_achievements (
  player_id INT REFERENCES player(player_id),
  achievement_id INT REFERENCES achievement(achievement_id),
  achievement_date DATE,
  PRIMARY KEY (player_id, achievement_id)
);

CREATE TABLE player (
    id INT PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    country VARCHAR(50),
    email VARCHAR(100) UNIQUE
);

CREATE TABLE achievement (
    id INT PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    description TEXT,
    points INT
);

CREATE TABLE trophy (
    id INT PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    description TEXT,
    image_url VARCHAR(200)
);
