tables.table_schema:
CREATE TABLE player_authentication (
    player_id SERIAL PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    password_hash VARCHAR(100) NOT NULL,
    email VARCHAR(100) UNIQUE
);

CREATE TABLE players (
    player_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    password_hash VARCHAR(100) NOT NULL
);

CREATE TABLE login_attempts (
    attempt_id INT PRIMARY KEY,
    player_id INT,
    attempt_datetime TIMESTAMP NOT NULL,
    FOREIGN KEY (player_id) REFERENCES players(player_id)
);

CREATE TABLE players (
    player_id SERIAL PRIMARY KEY,
    username VARCHAR(50) UNIQUE NOT NULL,
    password_hash VARCHAR(100) NOT NULL
);

CREATE TABLE sessions (
    session_id UUID PRIMARY KEY,
    player_id INT REFERENCES players(player_id) NOT NULL,
    login_timestamp TIMESTAMP NOT NULL
);

CREATE TABLE tokens (
    token_id SERIAL PRIMARY KEY,
    session_id UUID REFERENCES sessions(session_id) NOT NULL,
    token_value VARCHAR(200) UNIQUE NOT NULL
);

CREATE TABLE PlayerInfo (
    id INT PRIMARY KEY,
    username TEXT NOT NULL,
    password TEXT NOT NULL,
    email TEXT NOT NULL
);

CREATE TABLE PlayerStats (
    player_id INT REFERENCES PlayerInfo(id),
    level INT,
    score INT
);

CREATE TABLE SessionInfo (
    session_id INT PRIMARY KEY,
    player_id INT REFERENCES PlayerInfo(id),
    login_time TIMESTAMP,
    logout_time TIMESTAMP
);

CREATE TABLE AccessToken (
    token_id INT PRIMARY KEY,
    player_id INT REFERENCES PlayerInfo(id),
    access_token TEXT,
    expiry_date TIMESTAMP
);
