tables.table_schema:
CREATE TABLE PlayerActions (
    player_id INT,
    action_id INT,
    action_timestamp TIMESTAMP,
    action_description TEXT
);

CREATE TABLE Players (
    player_id UUID PRIMARY KEY,
    player_name VARCHAR(255) NOT NULL,
    player_email VARCHAR(255) UNIQUE NOT NULL,
    registration_date TIMESTAMP NOT NULL
);

CREATE TABLE PlayerActions (
    action_id UUID PRIMARY KEY,
    player_id UUID REFERENCES Players(player_id),
    action_type VARCHAR(50) NOT NULL,
    action_timestamp TIMESTAMP NOT NULL
);

CREATE TABLE player ( 
  player_id INT PRIMARY KEY, 
  username VARCHAR(50) NOT NULL, 
  email VARCHAR(100) NOT NULL, 
  registration_date TIMESTAMP NOT NULL 
);

CREATE TABLE game_session ( 
  session_id INT PRIMARY KEY, 
  player_id INT REFERENCES player(player_id), 
  start_time TIMESTAMP NOT NULL, 
  end_time TIMESTAMP 
);

CREATE TABLE player_actions ( 
  action_id INT PRIMARY KEY, 
  session_id INT REFERENCES game_session(session_id), 
  action_type VARCHAR(50) NOT NULL, 
  action_timestamp TIMESTAMP NOT NULL 
);

CREATE TABLE cheat_detection ( 
  detection_id INT PRIMARY KEY, 
  action_id INT REFERENCES player_actions(action_id), 
  detection_result VARCHAR(100) NOT NULL, 
  detection_timestamp TIMESTAMP NOT NULL 
);
