tables.table_schema:
CREATE TABLE customers (
  customer_id INT PRIMARY KEY,
  customer_name VARCHAR(255),
  email VARCHAR(255) NOT NULL
);

CREATE TABLE campaign_segments (
  segment_id INT PRIMARY KEY,
  segment_name VARCHAR(255),
  criteria VARCHAR(255) NOT NULL
);

CREATE TABLE customer_segments (
    segment_id INT PRIMARY KEY,
    segment_name VARCHAR(100) NOT NULL
);

CREATE TABLE customer_behavior (
    customer_id INT PRIMARY KEY,
    email_opened BOOLEAN NOT NULL,
    link_clicked BOOLEAN NOT NULL
);

CREATE TABLE campaign_data (
    campaign_id INT PRIMARY KEY,
    segment_id INT REFERENCES customer_segments(segment_id) ON DELETE CASCADE,
    start_date TIMESTAMP NOT NULL,
    end_date TIMESTAMP NOT NULL
);

CREATE TABLE email_content (
    email_id INT PRIMARY KEY,
    campaign_id INT REFERENCES campaign_data(campaign_id) ON DELETE CASCADE,
    subject VARCHAR(255) NOT NULL,
    content TEXT NOT NULL
);

CREATE TABLE email_receivers (
    email_id INT REFERENCES email_content(email_id) ON DELETE CASCADE,
    receiver_email VARCHAR(255) NOT NULL,
    send_date TIMESTAMP NOT NULL
);
