tables.table_schema:
CREATE TABLE loyalty_program (
  customer_id INT PRIMARY KEY,
  customer_name VARCHAR(50),
  total_spent DECIMAL(10, 2),
  loyalty_points INT
);

CREATE TABLE loyalty_program_customers (
  customer_id INT PRIMARY KEY,
  customer_name VARCHAR(255) NOT NULL,
  loyalty_points INT DEFAULT 0
);

CREATE TABLE loyalty_rewards (
  reward_id INT PRIMARY KEY,
  reward_name VARCHAR(255) NOT NULL,
  required_points INT NOT NULL
);

CREATE TABLE loyalty_program (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(50),
    loyalty_points INT
);

CREATE TABLE purchases (
    purchase_id INT PRIMARY KEY,
    customer_id INT,
    purchase_amount DECIMAL,
    purchase_date DATE
);

CREATE TABLE rewards (
    reward_id INT PRIMARY KEY,
    customer_id INT,
    reward_description VARCHAR(100),
    reward_points_required INT
);

CREATE TABLE loyalty_program.customers (
    customer_id UUID PRIMARY KEY,
    customer_name VARCHAR(255) NOT NULL,
    loyalty_points INT DEFAULT 0
);

CREATE TABLE loyalty_program.orders (
    order_id UUID PRIMARY KEY,
    customer_id UUID REFERENCES loyalty_program.customers(customer_id),
    order_total DECIMAL(10,2) NOT NULL,
    order_date TIMESTAMP
);

CREATE TABLE loyalty_program.rewards (
    reward_id UUID PRIMARY KEY,
    reward_name VARCHAR(255) NOT NULL,
    loyalty_points_required INT NOT NULL,
    description TEXT
);

CREATE TABLE loyalty_program.customer_rewards (
    customer_id UUID REFERENCES loyalty_program.customers(customer_id),
    reward_id UUID REFERENCES loyalty_program.rewards(reward_id),
    redeemed BOOLEAN DEFAULT FALSE,
    received_date DATE
);

CREATE TABLE customers (
   customer_id INT PRIMARY KEY,
   customer_name VARCHAR(50) NOT NULL,
   loyalty_points INT DEFAULT 0
);

CREATE TABLE rewards (
   reward_id INT PRIMARY KEY,
   reward_name VARCHAR(50) NOT NULL,
   point_cost INT,
   expiration_date DATE
);

CREATE TABLE loyalty_tiers (
   tier_id INT PRIMARY KEY,
   tier_name VARCHAR(50) NOT NULL,
   point_threshold INT
);
