tables.table_schema:
CREATE TABLE blocks (
    block_id INT PRIMARY KEY,
    block_hash VARCHAR(255) UNIQUE,
    previous_block_hash VARCHAR(255),
    timestamp TIMESTAMP
);

CREATE TABLE blockchain_blocks (
    block_id SERIAL PRIMARY KEY,
    block_hash VARCHAR(64),
    block_timestamp TIMESTAMP,
    num_transactions INT
);

CREATE TABLE block_transactions (
    block_id INT,
    transaction_id UUID,
    FOREIGN KEY (block_id) REFERENCES blockchain_blocks(block_id),
    FOREIGN KEY (transaction_id) REFERENCES transaction_details(transaction_id)
);

CREATE TABLE addresses (
  address_id UUID PRIMARY KEY,
  address VARCHAR(255),
  balance DECIMAL,
  transactions_count INT
);

CREATE TABLE holders (
  holder_id UUID PRIMARY KEY,
  holder_name VARCHAR(255),
  address_id UUID,
  FOREIGN KEY (address_id) REFERENCES addresses(address_id)
);
