CREATE TABLE blockchain_participants (
    participant_id INT PRIMARY KEY,
    participant_name VARCHAR(255) NOT NULL,
    public_key VARCHAR(512) UNIQUE NOT NULL
);

CREATE TABLE blockchain_participants (
    participant_id INT PRIMARY KEY,
    participant_name VARCHAR(255),
    public_key VARCHAR(255)
);

CREATE TABLE blockchain_participants (
    participant_id UUID PRIMARY KEY,
    participant_name VARCHAR(255) NOT NULL,
    public_key VARCHAR(255) UNIQUE
);

CREATE TABLE transactions (
    transaction_id UUID PRIMARY KEY,
    participant_id UUID REFERENCES blockchain_participants(participant_id),
    amount FLOAT NOT NULL
);

CREATE TABLE verification_requests (
    request_id UUID PRIMARY KEY,
    participant_id UUID REFERENCES blockchain_participants(participant_id),
    verified BOOLEAN DEFAULT FALSE
);

CREATE TABLE blockchain_participants (
    participant_id SERIAL PRIMARY KEY,
    participant_name VARCHAR(255) NOT NULL,
    public_key VARCHAR(255) NOT NULL,
    address VARCHAR(255) NOT NULL
);

CREATE TABLE participant_history (
    history_id SERIAL PRIMARY KEY,
    participant_id INT NOT NULL,
    action_description TEXT,
    action_timestamp TIMESTAMP
);

CREATE TABLE verification_logs (
    log_id SERIAL PRIMARY KEY,
    participant_id INT NOT NULL,
    verification_status BOOLEAN NOT NULL,
    verification_timestamp TIMESTAMP
);
