tables.table_schema:
CREATE TABLE inventory_levels (
    product_id INT,
    quantity INT,
    forecast_date DATE
);

CREATE TABLE supply_chain (
    supplier_id INT,
    product_id INT,
    delivery_date DATE,
    quantity_delivered INT
);

CREATE TABLE InventoryForecast (
    ProductID INT PRIMARY KEY,
    ProductName VARCHAR(255),
    ForecastDate DATE,
    ForecastQuantity INT
);

CREATE TABLE InventoryLevels (
    ProductID INT PRIMARY KEY,
    ProductName VARCHAR(255),
    CurrentQuantity INT,
    ReorderLevel INT
);

CREATE TABLE SupplyChainManagement (
    SupplierID INT PRIMARY KEY,
    SupplierName VARCHAR(255),
    ProductID INT,
    DeliveryDate DATE
);

CREATE TABLE forecasted_demand (
    product_id INT PRIMARY KEY,
    forecasted_quantity INT,
    forecasted_date DATE
);

CREATE TABLE current_inventory (
    product_id INT PRIMARY KEY,
    in_stock_quantity INT,
    last_updated TIMESTAMP
);

CREATE TABLE suppliers (
    supplier_id INT PRIMARY KEY,
    supplier_name VARCHAR(255),
    supplier_email VARCHAR(255),
    supplier_phone VARCHAR(20)
);

CREATE TABLE orders (
    order_id INT PRIMARY KEY,
    product_id INT,
    order_quantity INT,
    order_date DATE
);

CREATE TABLE Inventory (id INT PRIMARY KEY,
 product_name STRING,
 quantity INT);

CREATE TABLE Warehouse (id INT PRIMARY KEY,
 warehouse_name STRING,
 location STRING);

CREATE TABLE Supplier (id INT PRIMARY KEY,
 supplier_name STRING,
 contact_info STRING);
