tables.table_schema:
CREATE TABLE transactions (
    transaction_id INT PRIMARY KEY,
    amount DECIMAL,
    description TEXT,
    is_fraud BOOLEAN
);

CREATE TABLE machine_learning_models (
    model_id INT PRIMARY KEY,
    model_name TEXT,
    algorithm TEXT,
    accuracy DECIMAL
);

CREATE TABLE customers (
    customer_id UUID PRIMARY KEY,
    name TEXT,
    email TEXT,
    phone_number TEXT
);

CREATE TABLE users (
    user_id UUID PRIMARY KEY,
    name VARCHAR,
    email VARCHAR
);

CREATE TABLE models (
    model_id UUID PRIMARY KEY,
    model_name VARCHAR,
    model_accuracy DECIMAL
);

CREATE TABLE fraudulent_transactions (
   transaction_id SERIAL PRIMARY KEY,
   transaction_amount DECIMAL,
   transaction_date TIMESTAMPTZ,
   fraud_probability FLOAT
);

CREATE TABLE customer_details (
   customer_id SERIAL PRIMARY KEY,
   customer_name VARCHAR(255),
   customer_email VARCHAR(255),
   customer_address TEXT
);

CREATE TABLE fraud_detection_models (
   model_id SERIAL PRIMARY KEY,
   model_name VARCHAR(255),
   model_accuracy FLOAT,
   model_parameters JSON
);

CREATE TABLE transaction_logs (
   log_id SERIAL PRIMARY KEY,
   transaction_id INT REFERENCES fraudulent_transactions(transaction_id),
   log_details TEXT,
   log_timestamp TIMESTAMPTZ
);

CREATE TABLE flagging_rules (
   rule_id SERIAL PRIMARY KEY,
   rule_name VARCHAR(255),
   rule_condition TEXT,
   rule_action TEXT
);
