tables.table_schema:
CREATE TABLE price_elasticity (
    product_id INT PRIMARY KEY,
    price DECIMAL(10, 2) NOT NULL,
    demand INT NOT NULL,
    revenue DECIMAL(10, 2) NOT NULL
);

CREATE TABLE pricing_data (
    product_id INT PRIMARY KEY,
    price DECIMAL,
    quantity_sold INT
);

CREATE TABLE price_elasticity (
    product_id INT PRIMARY KEY,
    elasticity DECIMAL
);

CREATE TABLE CustomerFeedback (
    id SERIAL PRIMARY KEY,
    product_id INTEGER REFERENCES PriceElasticityData(id) NOT NULL,
    feedback_text TEXT,
    rating INTEGER
);

CREATE TABLE CompetitorPrices (
    id SERIAL PRIMARY KEY,
    product_name VARCHAR(255) NOT NULL,
    competitor_price NUMERIC NOT NULL
);

CREATE TABLE price_elasticity_data (
	id INT PRIMARY KEY,
	product_name STRING,
	price FLOAT,
	quantity_sold INT
);

CREATE TABLE customer_data (
	customer_id INT PRIMARY KEY,
	customer_name STRING,
	email STRING,
	address STRING
);

CREATE TABLE competitor_prices (
	product_id INT PRIMARY KEY,
	competitor_name STRING,
	price FLOAT
);

CREATE TABLE sales_trends (
	year INT,
	quarter INT,
	total_sales FLOAT
);

CREATE TABLE PricingStrategies (
    product_id INT PRIMARY KEY,
    price DECIMAL,
    sales_volume INT
);

CREATE TABLE ElasticityCoefficients (
    product_id INT PRIMARY KEY,
    elasticity_coefficient DECIMAL,
    demand_curve VARCHAR
);

CREATE TABLE CompetitorAnalysis (
    competitor_id INT PRIMARY KEY,
    competitor_name VARCHAR,
    market_share DECIMAL
);

CREATE TABLE MarketTrends (
    trend_id INT PRIMARY KEY,
    trend_description VARCHAR,
    impact_level DECIMAL
);

CREATE TABLE CustomerFeedback (
    feedback_id INT PRIMARY KEY,
    customer_id INT,
    feedback_text TEXT
);
