tables.table_schema:
CREATE TABLE customer_sentiments (
    customer_id INT PRIMARY KEY,
    sentiment_score FLOAT
);

CREATE TABLE social_media_posts (
    post_id INT PRIMARY KEY,
    post_content TEXT,
    post_date DATE,
    likes_count INT
);

CREATE TABLE post_likes (
    id INT PRIMARY KEY,
    post_id INT,
    customer_id INT
);

CREATE TABLE sentiment_analysis (
  id INT PRIMARY KEY,
  user_id INT,
  post TEXT,
  sentiment_score DECIMAL
);

CREATE TABLE user_profile (
  user_id INT PRIMARY KEY,
  username VARCHAR(50),
  email VARCHAR(50),
  location VARCHAR(100)
);

CREATE TABLE post_likes (
  id INT PRIMARY KEY,
  post_id INT,
  user_id INT
);

CREATE TABLE post_comments (
  id INT PRIMARY KEY,
  post_id INT,
  user_id INT,
  comment TEXT
);

CREATE TABLE sentiment_statistics (
  id INT PRIMARY KEY,
  date DATE,
  overall_sentiment_score DECIMAL
);
