tables.table_schema:
CREATE TABLE EmployeeAttrition ( 
    employee_id INT, 
    performance_score FLOAT, 
    engagement_score FLOAT 
);

CREATE TABLE employees (
 employee_id INT PRIMARY KEY,
 employee_name VARCHAR,
 performance_score INT,
 engagement_score INT
 );

CREATE TABLE attrition_predictions (
 employee_id INT PRIMARY KEY,
 attrition_probability FLOAT,
 prediction_date DATE
 );

CREATE TABLE EmployeePerformance (
    employee_id INT PRIMARY KEY,
    performance_score FLOAT,
    engagement_score FLOAT
);

CREATE TABLE AttritionData (
    employee_id INT PRIMARY KEY,
    attrition_status VARCHAR,
    department VARCHAR,
    years_in_company INT
);

CREATE TABLE PredictionResults (
    employee_id INT PRIMARY KEY,
    prediction_result VARCHAR
);

CREATE TABLE attrition_reasons (
    reason_id INT PRIMARY KEY,
    reason_description TEXT
);

CREATE TABLE attrition_predictions (
    prediction_id INT PRIMARY KEY,
    employee_id INT REFERENCES employees(employee_id),
    predicted_attrition_date DATE,
    predicted_score FLOAT
);

CREATE TABLE attrition_history (
    history_id INT PRIMARY KEY,
    employee_id INT REFERENCES employees(employee_id),
    attrition_date DATE,
    attrition_reason_id INT REFERENCES attrition_reasons(reason_id)
);

CREATE TABLE employees (
  employee_id INT PRIMARY KEY,
  employee_name VARCHAR(50),
  performance_rating INT,
  engagement_score INT
);

CREATE TABLE departments (
  department_id INT PRIMARY KEY,
  department_name VARCHAR(50)
);

CREATE TABLE attrition_reasons (
  reason_id INT PRIMARY KEY,
  reason_description TEXT
);

CREATE TABLE employee_attendance (
  attendance_id INT PRIMARY KEY,
  employee_id INT,
  date DATE,
  hours_worked DECIMAL
);

CREATE TABLE employee_reviews (
  review_id INT PRIMARY KEY,
  employee_id INT,
  review_date DATE,
  review_text TEXT
);
