tables.table_schema:
CREATE TABLE genetic_sequencing_data (
  id INT PRIMARY KEY,
  experiment_name VARCHAR(255),
  study_name VARCHAR(255),
  sequencing_data JSON
);

CREATE TABLE experiments (
    experiment_id UUID PRIMARY KEY,
    experiment_name STRING,
    experiment_date DATE
);

CREATE TABLE sequencing_data (
    data_id UUID PRIMARY KEY,
    experiment_id UUID REFERENCES experiments(experiment_id),
    sequence_data BLOB,
    sequence_date DATE
);

CREATE TABLE experiment_data (
    experiment_id INT PRIMARY KEY,
    experiment_name VARCHAR(255),
    data BLOB
);

CREATE TABLE analysis_results (
    analysis_id INT PRIMARY KEY,
    sample_id INT,
    analysis_data JSON,
    FOREIGN KEY (sample_id) REFERENCES sample_data(sample_id)
);

CREATE TABLE Experiment (
   experiment_id INT PRIMARY KEY,
   experiment_name VARCHAR(255) NOT NULL,
   start_date DATE,
   end_date DATE
);

CREATE TABLE Study (
   study_id INT PRIMARY KEY,
   study_name VARCHAR(255) NOT NULL,
   study_description TEXT
);

CREATE TABLE experiment(
  experiment_id UUID PRIMARY KEY,
  experiment_name STRING,
  experiment_date DATE
);

CREATE TABLE sample(
  sample_id UUID PRIMARY KEY,
  sample_name STRING,
  experiment_id UUID
);

CREATE TABLE sequencing_data(
  data_id UUID PRIMARY KEY,
  data_file_uuid UUID,
  data_sequence STRING,
  sample_id UUID
);

CREATE TABLE lab_technician(
  technician_id UUID PRIMARY KEY,
  technician_name STRING
);

CREATE TABLE study(
  study_id UUID PRIMARY KEY,
  study_name STRING,
  study_description STRING
);
