tables.table_schema:
CREATE TABLE Disease (
    id INT PRIMARY KEY,
    disease_name VARCHAR,
    description TEXT
);

CREATE TABLE GeneticMutation (
    id INT PRIMARY KEY,
    mutation_name VARCHAR,
    gene_name VARCHAR,
    effect VARCHAR
);

CREATE TABLE mutations (
    mutation_id INT PRIMARY KEY,
    gene_name VARCHAR(255) NOT NULL,
    mutation_type VARCHAR(50) NOT NULL
);

CREATE TABLE patients (
    patient_id INT PRIMARY KEY,
    patient_name VARCHAR(255) NOT NULL,
    age INT NOT NULL,
    gender VARCHAR(10) NOT NULL
);

CREATE TABLE disease_associations (
    disease_id INT,
    gene_name VARCHAR(255) NOT NULL,
    PRIMARY KEY (disease_id, gene_name)
);

CREATE TABLE Disease (id SERIAL PRIMARY KEY,
