tables.table_schema:
CREATE TABLE Biomarkers (
    biomarker_id INT PRIMARY KEY,
    biomarker_name VARCHAR(50),
    description TEXT,
    validation_method VARCHAR(100)
);

CREATE TABLE patients (
  patient_id INT PRIMARY KEY,
  patient_name VARCHAR,
  age INT,
  gender VARCHAR
);

CREATE TABLE treatments (
  treatment_id INT PRIMARY KEY,
  treatment_name VARCHAR,
  medication VARCHAR,
  dosage FLOAT
);

CREATE TABLE trials (
  trial_id INT PRIMARY KEY,
  trial_name VARCHAR,
  start_date DATE,
  end_date DATE
);

CREATE TABLE researchers (
  researcher_id INT PRIMARY KEY,
  researcher_name VARCHAR,
  specialty VARCHAR
);

CREATE TABLE trial_results (
  result_id INT PRIMARY KEY,
  trial_id INT,
  patient_id INT,
  outcome VARCHAR
);
