tables.table_schema:
CREATE TABLE drug_gene_interactions(
    id INT PRIMARY KEY,
    drug_name VARCHAR(255),
    gene_name VARCHAR(255),
    interaction_score FLOAT
);

CREATE TABLE Drug (
    drug_id INT PRIMARY KEY,
    drug_name VARCHAR(255),
    gene_id INT
);

CREATE TABLE Gene (
    gene_id INT PRIMARY KEY,
    gene_name VARCHAR(255),
    gene_type VARCHAR(50)
);

CREATE TABLE gene_information (
    gene_id INT PRIMARY KEY,
    gene_name VARCHAR(255),
    gene_type VARCHAR(50),
    gene_function TEXT
);

CREATE TABLE drug_information (
    drug_id INT PRIMARY KEY,
    drug_name VARCHAR(255),
    drug_class VARCHAR(50),
    drug_target VARCHAR(255)
);

CREATE TABLE patient_genotype (
    patient_id INT PRIMARY KEY,
    gene_id INT,
    genotype VARCHAR(10),
    FOREIGN KEY(gene_id) REFERENCES gene_information(gene_id)
);
