tables.table_schema:
CREATE TABLE trading_strategies (
    strategy_id SERIAL PRIMARY KEY,
    strategy_name VARCHAR(50),
    description TEXT
);

CREATE TABLE stock_splits (
    symbol VARCHAR,
    split_date DATE,
    split_ratio DECIMAL,
    PRIMARY KEY (symbol, split_date)
);

CREATE TABLE stock_dividends (
    symbol VARCHAR,
    ex_dividend_date DATE,
    dividend DECIMAL,
    PRIMARY KEY (symbol, ex_dividend_date)
);

CREATE TABLE trading_strategies (
    strategy_id INT,
    strategy_name VARCHAR,
    description TEXT,
    PRIMARY KEY (strategy_id)
);

CREATE TABLE stock_info (
  symbol VARCHAR(10),
  company_name VARCHAR(100),
  sector VARCHAR(50),
  industry VARCHAR(50)
);

CREATE TABLE trading_strategies (
  strategy_id SERIAL PRIMARY KEY,
  strategy_name VARCHAR(50),
  description TEXT
);

CREATE TABLE strategy_results (
  result_id SERIAL PRIMARY KEY,
  strategy_id INT,
  date DATE,
  cumulative_return DECIMAL
);
