tables.table_schema:
CREATE TABLE Investors (
  id INT PRIMARY KEY,
  name VARCHAR(255),
  email VARCHAR(100),
  phone VARCHAR(15)
);

CREATE TABLE portfolio (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    description TEXT
);

CREATE TABLE performance (
    id INT PRIMARY KEY,
    investment_id INT,
    date DATE,
    start_value DECIMAL(10,2),
    end_value DECIMAL(10,2),
    return DECIMAL(5,2),
    FOREIGN KEY (investment_id) REFERENCES investment(id)
);

CREATE TABLE sector (
    id INT PRIMARY KEY,
    name VARCHAR(50)
);
