tables.table_schema:
CREATE TABLE FinancialRatios (
  id INT PRIMARY KEY,
  profit_margin DECIMAL,
  return_on_assets DECIMAL,
  current_ratio DECIMAL
);

CREATE TABLE PerformanceIndicators (
  id INT PRIMARY KEY,
  debt_to_equity_ratio DECIMAL,
  earnings_per_share DECIMAL,
  operating_margin DECIMAL
);

CREATE TABLE financial_ratios (
    attribute1 INT,
    attribute2 TEXT,
    attribute3 DECIMAL
);

CREATE TABLE performance_indicators (
    indicator_id SERIAL PRIMARY KEY,
    indicator_name TEXT,
    indicator_value DECIMAL
);

CREATE TABLE decision_making (
    decision_id SERIAL PRIMARY KEY,
    decision_name TEXT,
    decision_date DATE
);

CREATE TABLE Decision_Making (
    id INT PRIMARY KEY,
    decision_name VARCHAR(50) NOT NULL,
    date DATE NOT NULL,
    decision_description TEXT NOT NULL
);

CREATE TABLE liquidity_ratios (
  id INT PRIMARY KEY,
  name VARCHAR(255),
  current_ratio DECIMAL,
  quick_ratio DECIMAL
);

CREATE TABLE solvency_ratios (
  id INT PRIMARY KEY,
  name VARCHAR(255),
  debt_ratio DECIMAL,
  equity_ratio DECIMAL
);
