tables.table_schema:
CREATE TABLE reconciliation_data (
    internal_id INT PRIMARY KEY,
    internal_data TEXT,
    external_id INT,
    external_data TEXT
);

CREATE TABLE reconciliation_data_sources (
    id INT PRIMARY KEY,
    internal_data VARCHAR(255),
    external_data VARCHAR(255)
);

CREATE TABLE reconciliation_logs (
    log_id INT PRIMARY KEY,
    action VARCHAR(50),
    timestamp TIMESTAMPTZ
);

CREATE TABLE internal_data_sources (
    id INT PRIMARY KEY,
    source_name VARCHAR(100),
    data_type VARCHAR(50),
    last_updated TIMESTAMP
);

CREATE TABLE external_data_sources (
    id INT PRIMARY KEY,
    source_name VARCHAR(100),
    data_type VARCHAR(50),
    last_updated TIMESTAMP
);

CREATE TABLE reconciliation_logs (
    log_id INT PRIMARY KEY,
    job_id INT,
    message TEXT,
    log_timestamp TIMESTAMP
);

CREATE TABLE internal_data_sources(
    id INT PRIMARY KEY,
    source_name VARCHAR(50),
    connection_string VARCHAR(255)
);

CREATE TABLE external_data_sources(
    id INT PRIMARY KEY,
    source_name VARCHAR(50),
    api_key VARCHAR(100),
    endpoint_url VARCHAR(255)
);

CREATE TABLE reconciliation_logs(
    log_id INT PRIMARY KEY,
    reconciliation_date DATE,
    status VARCHAR(20),
    error_message TEXT
);

CREATE TABLE reconciliation_settings(
    setting_id INT PRIMARY KEY,
    rule_name VARCHAR(50),
    match_threshold INT
);
