tables.table_schema:
CREATE TABLE trading_reports (
    report_id UUID PRIMARY KEY,
    report_name VARCHAR(255),
    report_date DATE
);

CREATE TABLE trading_dashboards (
    dashboard_id UUID PRIMARY KEY,
    dashboard_name VARCHAR(255),
    dashboard_type VARCHAR(50)
);

CREATE TABLE stocks (
  stock_id INT PRIMARY KEY,
  stock_symbol VARCHAR(50) NOT NULL,
  stock_name VARCHAR(100) NOT NULL,
  stock_price DECIMAL(10, 2)
);

CREATE TABLE reports (
  report_id INT PRIMARY KEY,
  report_name VARCHAR(100) NOT NULL,
  report_date DATE,
  report_type VARCHAR(50)
);

CREATE TABLE traders (
    trader_id UUID PRIMARY KEY,
    trader_name VARCHAR(100) NOT NULL,
    trader_location VARCHAR(50) NOT NULL,
    trader_email VARCHAR(100) NOT NULL
);

CREATE TABLE dashboards (
    dashboard_id UUID PRIMARY KEY,
    dashboard_name VARCHAR(100) NOT NULL,
    dashboard_layout JSONB NOT NULL
);

CREATE TABLE traders (
  trader_id INT PRIMARY KEY,
  trader_name STRING
);

CREATE TABLE positions (
  position_id INT PRIMARY KEY,
  trader_id INT,
  symbol STRING,
  quantity INT
);

CREATE TABLE performance_metrics (
  metric_id INT PRIMARY KEY,
  trader_id INT,
  metric_name STRING,
  metric_value DECIMAL
);
