tables.table_schema:
CREATE TABLE distances ( 
 id INT PRIMARY KEY, 
 point1 GEOGRAPHY, 
 point2 GEOGRAPHY 
 );

CREATE TABLE location_data (
    location_id UUID PRIMARY KEY,
    latitude DECIMAL,
    longitude DECIMAL
 );

CREATE TABLE distance_calculation (
    calculation_id UUID PRIMARY KEY,
    location1_id UUID,
    location2_id UUID,
    distance DECIMAL
 );

CREATE TABLE locations (
    location_id INT PRIMARY KEY,
    location_name VARCHAR(255),
    latitude FLOAT,
    longitude FLOAT
);

CREATE TABLE distances (
    distance_id INT PRIMARY KEY,
    from_location_id INT,
    to_location_id INT,
    distance FLOAT
);

CREATE TABLE calculations (
    calculation_id INT PRIMARY KEY,
    calculation_name VARCHAR(255),
    result FLOAT
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    user_name VARCHAR(255),
    email VARCHAR(255)
);

CREATE TABLE logs (
    log_id INT PRIMARY KEY,
    log_message TEXT,
    created_at TIMESTAMP
);
