tables.table_schema:
CREATE TABLE geospatial_analysis (
  location_id INT PRIMARY KEY,
  latitude DECIMAL,
  longitude DECIMAL,
  location_name TEXT
);

CREATE TABLE locations (
    location_id INT PRIMARY KEY,
    latitude DECIMAL,
    longitude DECIMAL,
    address TEXT
);

CREATE TABLE geospatial_data (
  data_id SERIAL PRIMARY KEY,
  location_id INTEGER REFERENCES locations(location_id),
  timestamp TIMESTAMP,
  data_value DECIMAL
);

CREATE TABLE location_patterns (
  pattern_id SERIAL PRIMARY KEY,
  pattern_name VARCHAR(50),
  pattern_description TEXT,
  location_id INTEGER REFERENCES locations(location_id)
);

CREATE TABLE locations (
    location_id UUID PRIMARY KEY,
    latitude DECIMAL,
    longitude DECIMAL,
    address TEXT
);

CREATE TABLE geospatial_data (
    data_id UUID PRIMARY KEY,
    location_id UUID,
    data_type TEXT,
    data_value JSONB
);

CREATE TABLE spatial_analytics (
    analysis_id UUID PRIMARY KEY,
    data_id UUID,
    analysis_text TEXT,
    analysis_result JSONB
);

CREATE TABLE spatial_patterns (
    pattern_id UUID PRIMARY KEY,
    analysis_id UUID,
    pattern_type TEXT,
    pattern_result JSONB
);
