tables.table_schema:
CREATE TABLE spatial_joins (
    id INT PRIMARY KEY,
    location GEOMETRY,
    data_source VARCHAR(255)
);

CREATE TABLE Spatial_Joins_Table1 (
    id INT PRIMARY KEY,
    location1 GEOMETRY(POINT),
    data1 VARCHAR(255)
);

CREATE TABLE Spatial_Joins_Table2 (
    id INT PRIMARY KEY,
    location2 GEOMETRY(POINT),
    data2 VARCHAR(255)
);

CREATE TABLE geospatial_data (id INT,
                           location GEOGRAPHY,
                           data3 TEXT);

CREATE TABLE spatial_analysis (id INT,
                            analysis_id INT,
                            result_id INT);

CREATE TABLE analyzed_data (id INT,
                            result_id INT,
                            data3 TEXT);
