    tile_id SERIAL PRIMARY KEY,
    tile_data BYTEA
);

CREATE TABLE tile_metadata (
    tile_id SERIAL PRIMARY KEY,
    tile_name VARCHAR(255),
    created_at TIMESTAMP,
    last_updated_at TIMESTAMP
);

CREATE TABLE mapping_tiles (
    tile_id INT PRIMARY KEY,
    tile_data BYTEA,
    timestamp TIMESTAMP
);

CREATE TABLE tile_metadata (
    tile_id INT PRIMARY KEY,
    tile_name TEXT,
    created_by TEXT,
    created_on DATE
);

CREATE TABLE tile_usage (
    usage_id INT PRIMARY KEY,
    tile_id INT REFERENCES mapping_tiles(tile_id),
    app_name TEXT,
    usage_count INT
);

CREATE TABLE mapping_tiles (
    tile_id UUID PRIMARY KEY,
    tile_data BYTEA NOT NULL
);

CREATE TABLE tile_metadata (
    tile_id UUID PRIMARY KEY,
    creation_timestamp TIMESTAMP NOT NULL,
    creator_name VARCHAR(50)
);

CREATE TABLE tile_images (
    image_id UUID PRIMARY KEY,
    tile_id UUID REFERENCES mapping_tiles(tile_id),
    image_data BYTEA NOT NULL
);

CREATE TABLE tile_hierarchy (
    hierarchy_id UUID PRIMARY KEY,
    tile_id UUID REFERENCES mapping_tiles(tile_id),
    parent_tile_id UUID REFERENCES mapping_tiles(tile_id),
    level INTEGER NOT NULL
);

CREATE TABLE mapping_tiles (
    tile_id INT PRIMARY KEY,
    tile_data BYTEA,
    tile_metadata JSON
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(100)
);

CREATE TABLE tilesets (
    tileset_id INT PRIMARY KEY,
    tileset_name VARCHAR(100),
    tileset_description TEXT
);

CREATE TABLE tile_usage (
    usage_id INT PRIMARY KEY,
    tile_id INT REFERENCES mapping_tiles (tile_id),
    user_id INT REFERENCES users (user_id),
    usage_date TIMESTAMP
);

CREATE TABLE user_preferences (
    preference_id INT PRIMARY KEY,
    user_id INT REFERENCES users (user_id),
    preference_key VARCHAR(50),
    preference_value VARCHAR(100)
);
