tables.table_schema:
CREATE TABLE financial_data (
    id INT PRIMARY KEY,
    account_name VARCHAR(255),
    transaction_date DATE,
    amount DECIMAL(10,2)
);

CREATE TABLE budgeting_data (
    id INT PRIMARY KEY,
    account_name VARCHAR(255),
    budget_amount DECIMAL(10,2),
    actual_amount DECIMAL(10,2)
);

CREATE TABLE forecasting_data (
    id INT PRIMARY KEY,
    account_name VARCHAR(255),
    forecast_date DATE,
    forecast_amount DECIMAL(10,2)
);

CREATE TABLE budget_data (
   id INT PRIMARY KEY,
   category VARCHAR(255),
   amount DECIMAL(10, 2),
   date DATE
);

CREATE TABLE financial_reports (
   id INT PRIMARY KEY,
   report_date DATE,
   generated_by VARCHAR(255),
   report_text TEXT
);
