tables.table_schema:
CREATE TABLE sentiment_analysis (
    id UUID PRIMARY KEY,
    username VARCHAR(50),
    post_text TEXT,
    sentiment_score FLOAT
);

CREATE TABLE customer_feedback (
    id UUID PRIMARY KEY,
    customer_name VARCHAR(50),
    feedback_text TEXT,
    feedback_date TIMESTAMP
);

CREATE TABLE social_media_users (
    user_id INT PRIMARY KEY,
    username STRING,
    email STRING,
    join_date DATE
);

CREATE TABLE Likes (
    like_id INT,
    post_id INT,
    user_id INT,
    like_date TIMESTAMP
);

CREATE TABLE Users (
    user_id INT,
    user_name VARCHAR(50),
    email VARCHAR(50),
    join_date TIMESTAMP
);

CREATE TABLE SentimentAnalysis (
    analysis_id INT,
    post_id INT,
    sentiment_score DECIMAL(5, 2),
    analysis_date TIMESTAMP
);
